export var fontString = {
	icomoon: ''
}
export function generateFontTemplate(b64String:string){
	return `@font-face {
			font-family:"customicomoon";
			src: url(data:application/x-font-woff;charset=utf-8;base64,${b64String}) format('woff');
			font-style:normal;
			font-weight:normal;
		}

		[class^="pa-icon-"], [class*=" pa-icon-"] {
		    /* use !important to prevent issues with browser extensions that change fonts */
		    font-family: 'customicomoon' !important;
		    speak: none;
		    font-style: normal;
		    font-weight: normal;
		    font-variant: normal;
		    text-transform: none;
		    line-height: 1;

		    /* Better Font Rendering =========== */
		    -webkit-font-smoothing: antialiased;
		    -moz-osx-font-smoothing: grayscale;
		}

		.pa-icon-images:before {
		    content: "\\e900";
		}
		.pa-icon-stack:before {
		    content: "\\e902";
		}
		.pa-icon-mug:before {
		    content: "\\e903";
		}
		.pa-icon-menu4:before {
		    content: "\\e904";
		}
		.pa-icon-exit:before {
		    content: "\\e905";
		}
		.pa-icon-share:before {
		    content: "\\e901";
		}
		`;
}
